function openAiSummery(currentObj) { const el1 = document.querySelector('.expandDetails'); el1.classList.toggle('open'); const el2 = document.querySelector('.readMoreBtn'); el2.classList.toggle('expandedIcon'); if (typeof window.aiSummeryClickEvent === "function") { if (el1.classList.contains('open')) { event_label = "Expand|‘Inspired by Nepal’s Gen Z protests’: Delhi Police on Congress’ stir at AI Summit|Desktop"; aiSummeryClickEvent(event_label); } } if (typeof window.aiSummaryAppGAEvent === "function") { if (el1.classList.contains('open')) { event_label = "Expand|‘Inspired by Nepal’s Gen Z protests’: Delhi Police on Congress’ stir at AI Summit|iOS"; aiSummaryAppGAEvent(event_label); } } }

const voteBox = document.querySelector(".vote-box"); const articleId = voteBox.dataset.articleId; const likeBtn = voteBox.querySelector(".like-btn"); const dislikeBtn = voteBox.querySelector(".dislike-btn"); const cookieKey = "vote_" + articleId; /* Cookie helper functions */ function setCookie(name, value, days = 365) { const expires = new Date(Date.now() + days * 864e5).toUTCString(); document.cookie = `${name}=${value}; expires=${expires}; path=/`; } function getCookie(name) { const match = document.cookie.match(new RegExp('(^| )' + name + '=([^;]+)')); return match ? match[2] : null; } /* Restore previous vote from cookies */ const savedVote = getCookie(cookieKey); if (savedVote === "like") likeBtn.classList.add("active"); if (savedVote === "dislike") dislikeBtn.classList.add("active"); function handleVote(voteType) { /* Save vote in cookie */ setCookie(cookieKey, voteType, 30); /* Update UI */ likeBtn.classList.remove("active"); dislikeBtn.classList.remove("active"); if (voteType === "like") likeBtn.classList.add("active"); else dislikeBtn.classList.add("active"); /* (Optional) Send to API */ /* fetch(`https://your-api.com/api/article/${articleId}/vote`, { method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify({ vote: voteType }) }); */ } /* likeBtn.addEventListener("click", () => handleVote("like")); dislikeBtn.addEventListener("click", () => handleVote("dislike")); */ function likeBtnEvent (currentObj) { if (typeof window.aiSummeryClickEvent === "function") { const articleId = voteBox.dataset.articleId; const cookieKey = "vote_" + articleId; const savedVote = getCookie(cookieKey); if (savedVote == 'dislike') { event_label = "Thumbs Up|‘Inspired by Nepal’s Gen Z protests’: Delhi Police on Congress’ stir at AI Summit|Desktop"; aiSummeryClickEvent(event_label); } } if (typeof window.aiSummaryAppGAEvent === "function") { const articleId = voteBox.dataset.articleId; const cookieKey = "vote_" + articleId; const savedVote = getCookie(cookieKey); if (savedVote == 'dislike') { event_label = "Thumbs Up|‘Inspired by Nepal’s Gen Z protests’: Delhi Police on Congress’ stir at AI Summit|iOS"; aiSummaryAppGAEvent(event_label); } } handleVote("like"); } function dislikeBtnEvent (currentObj) { if (typeof window.aiSummeryClickEvent === "function") { const articleId = voteBox.dataset.articleId; const cookieKey = "vote_" + articleId; const savedVote = getCookie(cookieKey); if (savedVote == 'like') { event_label = "Thumbs Down|‘Inspired by Nepal’s Gen Z protests’: Delhi Police on Congress’ stir at AI Summit|Desktop"; aiSummeryClickEvent(event_label); } } if (typeof window.aiSummaryAppGAEvent === "function") { const articleId = voteBox.dataset.articleId; const cookieKey = "vote_" + articleId; const savedVote = getCookie(cookieKey); if (savedVote == 'like') { event_label = "Thumbs Down|‘Inspired by Nepal’s Gen Z protests’: Delhi Police on Congress’ stir at AI Summit|iOS"; aiSummaryAppGAEvent(event_label); } } handleVote("dislike"); }

The Youth Congress protest at the AI Summit venue in Delhi was inspired by Nepal’s violent Gen Z movement that toppled the government there, Delhi Police told a court, claiming there was a larger conspiracy behind the demonstration.

“This is a larger conspiracy that has taken inspiration from the Gen Z protests in Nepal. The accused need to be confronted with each other as well as with digital evidence. This is a very important investigation,” police said as it sought custody of the arrested protestors, according to a report by CNN-News18.

